<template>
  <div class="avatar-selector">
    <!-- ÂΩìÂâçÂ§¥ÂÉèÊòæÁ§∫ -->
    <div class="current-avatar" @click="showSelector = true">
      <a-avatar :size="size" :src="currentAvatar" class="avatar-display">
        <template #icon>
          <UserOutlined v-if="!currentAvatar" />
        </template>
      </a-avatar>
      <div v-if="editable" class="avatar-edit-overlay">
        <CameraOutlined />
      </div>
    </div>

    <!-- Â§¥ÂÉèÈÄâÊã©Âô®ÂºπÁ™ó -->
    <a-modal
      v-model:open="showSelector"
      title="ÈÄâÊã©Â§¥ÂÉè"
      :width="600"
      @ok="handleConfirm"
      @cancel="handleCancel"
    >
      <div class="avatar-selector-content">
        <!-- ÂèØÁà±Âä®Áâ© -->
        <div class="avatar-category">
          <h4>üê± ÂèØÁà±Âä®Áâ©</h4>
          <div class="avatar-grid">
            <div
              v-for="avatar in animalAvatars"
              :key="avatar.id"
              class="avatar-option"
              :class="{ active: selectedAvatar === avatar.id }"
              @click="selectAvatar(avatar.id)"
            >
              <a-avatar :size="50" :src="avatar.url">
                <template #icon><UserOutlined /></template>
              </a-avatar>
              <span class="avatar-label">{{ avatar.name }}</span>
            </div>
          </div>
        </div>

        <!-- Âç°ÈÄöËßíËâ≤ -->
        <div class="avatar-category">
          <h4>ü§ñ Âç°ÈÄöËßíËâ≤</h4>
          <div class="avatar-grid">
            <div
              v-for="avatar in characterAvatars"
              :key="avatar.id"
              class="avatar-option"
              :class="{ active: selectedAvatar === avatar.id }"
              @click="selectAvatar(avatar.id)"
            >
              <a-avatar :size="50" :src="avatar.url">
                <template #icon><UserOutlined /></template>
              </a-avatar>
              <span class="avatar-label">{{ avatar.name }}</span>
            </div>
          </div>
        </div>

        <!-- Ë°®ÊÉÖÂåÖ -->
        <div class="avatar-category">
          <h4>üòä Ë°®ÊÉÖÂåÖ</h4>
          <div class="avatar-grid">
            <div
              v-for="avatar in emojiAvatars"
              :key="avatar.id"
              class="avatar-option"
              :class="{ active: selectedAvatar === avatar.id }"
              @click="selectAvatar(avatar.id)"
            >
              <a-avatar :size="50" :src="avatar.url">
                <template #icon><UserOutlined /></template>
              </a-avatar>
              <span class="avatar-label">{{ avatar.name }}</span>
            </div>
          </div>
        </div>

        <!-- Ëá™ÂÆö‰πâ‰∏ä‰º† -->
        <div class="custom-upload">
          <a-upload
            :show-upload-list="false"
            accept="image/*"
            :before-upload="handleCustomUpload"
            class="custom-upload-btn"
          >
            <a-button>
              <UploadOutlined />
              ‰∏ä‰º†Ëá™ÂÆö‰πâÂ§¥ÂÉè
            </a-button>
          </a-upload>
        </div>
      </div>
    </a-modal>
  </div>
</template>

<script setup lang="ts">
import { ref, computed, watch, onMounted } from 'vue'
import { message } from 'ant-design-vue'
import { UserOutlined, CameraOutlined, UploadOutlined } from '@ant-design/icons-vue'

interface Props {
  modelValue?: string
  size?: number | 'large' | 'small' | 'default'
  editable?: boolean
}

interface Emits {
  (e: 'update:modelValue', value: string): void
  (e: 'change', value: string): void
}

const props = withDefaults(defineProps<Props>(), {
  size: 'default',
  editable: true
})

const emit = defineEmits<Emits>()

const showSelector = ref(false)
const selectedAvatar = ref('')

// Êú¨Âú∞Â§¥ÂÉèÊï∞ÊçÆÔºà‰Ωú‰∏∫fallbackÔºâ
const animalAvatars = [
  {
    id: 'cat',
    name: 'Â∞èÁå´',
    url: 'data:image/svg+xml;base64,'
  },
  {
    id: 'dog',
    name: 'Â∞èÁãó',
    url: 'data:image/svg+xml;base64,'
  },
  {
    id: 'bear',
    name: 'Â∞èÁÜä',
    url: 'data:image/svg+xml;base64,'
  },
  {
    id: 'rabbit',
    name: 'Â∞èÂÖî',
    url: 'data:image/svg+xml;base64,'
  },
  {
    id: 'panda',
    name: 'ÁÜäÁå´',
    url: 'data:image/svg+xml;base64,'
  },
  {
    id: 'fox',
    name: 'Â∞èÁãê',
    url: 'data:image/svg+xml;base64,'
  }
]

const characterAvatars = [
  {
    id: 'robot',
    name: 'Êú∫Âô®‰∫∫',
    url: 'data:image/svg+xml;base64,'
  },
  {
    id: 'princess',
    name: 'ÂÖ¨‰∏ª',
    url: 'data:image/svg+xml;base64,'
  },
  {
    id: 'superhero',
    name: 'Ë∂Ö‰∫∫',
    url: 'data:image/svg+xml;base64,'
  }
]

const emojiAvatars = [
  {
    id: 'smile',
    name: 'Á¨ëËÑ∏',
    url: 'data:image/svg+xml;base64,'
  },
  {
    id: 'wink',
    name: 'Áú®Áúº',
    url: 'data:image/svg+xml;base64,'
  },
  {
    id: 'cool',
    name: 'ÈÖ∑ÁÇ´',
    url: 'data:image/svg+xml;base64,'
  }
]

// ÂΩìÂâçÊòæÁ§∫ÁöÑÂ§¥ÂÉèURL
const currentAvatar = computed(() => {
  if (!props.modelValue) {
    return animalAvatars[0].url // ÈªòËÆ§Â∞èÁå´Â§¥ÂÉè
  }
  
  // Â¶ÇÊûúÊòØÂÆåÊï¥ÁöÑURLÔºåÁõ¥Êé•ËøîÂõû
  if (props.modelValue.startsWith('http') || props.modelValue.startsWith('data:')) {
    return props.modelValue
  }
  
  // Â¶ÇÊûúÊòØÂ§¥ÂÉèIDÔºåÊü•ÊâæÂØπÂ∫îÁöÑURL
  const allAvatars = [...animalAvatars, ...characterAvatars, ...emojiAvatars]
  const avatar = allAvatars.find(a => a.id === props.modelValue)
  return avatar ? avatar.url : animalAvatars[0].url
})

// ÈÄâÊã©Â§¥ÂÉè
const selectAvatar = (avatarId: string) => {
  selectedAvatar.value = avatarId
}

// ÂàùÂßãÂåñÈÄâ‰∏≠ÁöÑÂ§¥ÂÉè
watch(() => props.modelValue, (newValue) => {
  selectedAvatar.value = newValue || ''
}, { immediate: true })

// Á°ÆËÆ§ÈÄâÊã©
const handleConfirm = () => {
  if (selectedAvatar.value) {
    emit('update:modelValue', selectedAvatar.value)
    emit('change', selectedAvatar.value)
  }
  showSelector.value = false
}

// ÂèñÊ∂àÈÄâÊã©
const handleCancel = () => {
  selectedAvatar.value = props.modelValue || ''
  showSelector.value = false
}

// Ëá™ÂÆö‰πâ‰∏ä‰º†
const handleCustomUpload = (file: File) => {
  return new Promise((resolve) => {
    const reader = new FileReader()
    reader.onload = (e) => {
      const result = e.target?.result as string
      selectedAvatar.value = result
      message.success('ÂõæÁâáÂ∑≤ÈÄâÊã©ÔºåÁÇπÂáªÁ°ÆÂÆö‰øùÂ≠ò')
      resolve(false) // ÈòªÊ≠¢ÈªòËÆ§‰∏ä‰º†Ë°å‰∏∫
    }
    reader.readAsDataURL(file)
  })
}
</script>

