  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 16px;
}

.avatar-option {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 12px;
  border: 2px solid #d9d9d9;
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.3s ease;
  background: white;
}

.avatar-option:hover {
  border-color: #1890ff;
  transform: scale(1.05);
  box-shadow: 0 2px 8px rgba(24, 144, 255, 0.2);
}

.avatar-option.active {
  border-color: #1890ff;
  border-width: 3px;
  box-shadow: 0 0 0 2px rgba(24, 144, 255, 0.2);
  background: #f0f8ff;
}

.avatar-label {
  margin-top: 8px;
  font-size: 12px;
  color: #666;
  text-align: center;
}

